body {
    font-family: Arial, sans-serif;
    font-size: 12px;
    margin: 20px;
    background-color: #fff;
}
.container {
    width: 800px;
    margin: 0 auto;
}

/* Styles for Debit Note */
.debit-note {
    border: 2px solid black;
    margin-bottom: 40px;
}
.header-title {
    text-align: center;
    font-weight: bold;
    border-bottom: 1px solid black;
    padding: 5px;
    font-size: 14px;
}
.top-section {
    display: flex;
    border-bottom: 1px solid black;
}
.top-left {
    flex: 1;
    padding: 5px;
    border-right: 1px solid black;
}
.top-right {
    width: 300px;
    padding: 5px;
}
.main-content {
    display: flex;
}
.content-left {
    flex: 1.2;
    padding: 5px;
    border-right: 1px solid black;
}
.content-right {
    flex: 0.8;
    padding: 0;
    display: flex;
    flex-direction: column;
}

table.details-table {
    width: 100%;
    border-collapse: collapse;
}
table.details-table td {
    vertical-align: top;
    padding: 2px 0;
}
.label {
    width: 120px;
    font-weight: bold;
}
.label span {
    display: block;
    font-weight: normal;
    font-style: italic;
    font-size: 10px;
}
.separator {
    width: 10px;
    text-align: center;
}

.yellow-box {
    background-color: #ffff00;
    padding: 5px;
    margin: 5px -5px 5px -5px; /* Extend to edges */
    padding-left: 5px;
}
.yellow-box table td {
    padding: 2px 0;
}

.financial-table {
    width: 100%;
    border-collapse: collapse;
    padding: 5px;
}
.financial-table td {
    padding: 2px 5px;
}
.border-top {
    border-top: 1px solid black;
}
.text-right {
    text-align: right;
}

.terbilang-section {
    border-top: 1px solid black;
    padding: 5px;
}
.payment-section {
    padding: 5px;
    margin-top: 10px;
}
.signature-section {
    text-align: center;
    margin-top: 30px;
    margin-bottom: 10px;
    border-top: 1px solid black;
    padding-top: 5px;
}

/* Styles for Kwitansi */
.kwitansi {
    border: 2px solid black;
    padding: 20px;
}
.kwitansi-header {
    text-align: center;
    margin-bottom: 20px;
    position: relative;
}
.kwitansi-title {
    font-weight: bold;
    text-decoration: underline;
    font-size: 16px;
}
.kwitansi-no {
    font-size: 12px;
    margin-top: 5px;
}
.kwitansi-body table {
    width: 100%;
}
.kwitansi-body td {
    padding: 5px 0;
    vertical-align: top;
}
.money-text {
    font-weight: bold;
    font-style: italic;
    font-size: 14px;
}
.total-amount {
    font-size: 14px;
}
.kwitansi-footer {
    margin-top: 30px;
    display: flex;
    justify-content: flex-end;
}
.signature-box {
    text-align: center;
    width: 250px;
}
.signature-name {
    margin-top: 60px;
    text-decoration: underline;
    font-weight: bold;
}

